#ifndef COMMAND_H
#define COMMAND_H

#include <string>

// type of game move
enum class CommandType
{
	// gameplay commands
	LEFT = 0,
	RIGHT,
	DOWN,
	CLOCKWISE,
	COUNTERCLOCKWISE,
	DROP,
	LEVELUP,
	LEVELDOWN,
	NORANDOM_FILE,
	RANDOM,
	SEQUENCE_FILE,
	RESTART,
	HINT,

	// test commands
	I,
	J,
	L,
	S,
	Z,
	O,
	T,

	// program control commands
	QUIT,

	UNDEFINED_COMMAND
};

// commands are generated by views and interpreted
// by a CommandInterpreter instance
struct Command
{
	Command();
	Command(CommandType, bool = false);
	Command(std::string, CommandType, bool = false);
	CommandType type;
	// silent commands sent to a Subject class should not
	// result in the Subject notifying its Observers
	// commands are NOT silent by default
	bool silent;
	// for some commands, a message string may be desired
	std::string message;
};

typedef CommandType CMD;

#endif // COMMAND_H
